mapper.select:
SELECT tqms_dept_name deptName,sum(need_report_count) needWriteCount,sum(complete_report_count) completeWriteCount,sum(not_report_count) notWriteCount, CONCAT(IFNULL(CAST(AVG(complete_report_country_rate) AS DECIMAL(18,2)),0),'%') completeReportCountryRate, CAST(sum(average_in_hospital_day) AS DECIMAL(18,2)) averageInHospitalDay, CAST(sum(average_in_hospital_fee) AS DECIMAL(18,2)) averageInHospitalFee, CAST(sum(average_drug_fee) AS DECIMAL(18,2)) averageDrugFee, CAST(sum(average_operation_treatment_fee) AS DECIMAL(18,2)) averageOperationFee, CAST(sum(average_disposable_consumable) AS DECIMAL(18,2)) averageDisposableConsumableFee, CONCAT(CAST(sum(mortality) AS DECIMAL(18,2)),'%') mortality, CONCAT(CAST(sum(operation_complication_rate) AS DECIMAL(18,2)),'%') complicationRate FROM `q_single_disease_statistic_dept`
year_month_remark >= #{qSingleDiseaseTakeStatisticAnalysisParam.dateStart} and year_month_remark <= #{qSingleDiseaseTakeStatisticAnalysisParam.dateEnd}
and category_id = #{qSingleDiseaseTakeStatisticAnalysisParam.categoryId}
group by tqms_dept

mapper.sql:
SELECT category_id categoryId,single_disease_name disease,sum(need_report_count) needReportCount, sum(complete_report_count) completeReportCount,sum(not_report_count) notReportCount, CONCAT(IFNULL(CAST(AVG(complete_report_country_rate) AS DECIMAL(18,2)),0),'%') completeReportCountryRate, CAST(sum(average_in_hospital_day) AS DECIMAL(18,2)) averageInHospitalDay, CAST(sum(average_in_hospital_fee) AS DECIMAL(18,2)) averageInHospitalFee, CAST(sum(average_drug_fee) AS DECIMAL(18,2)) averageDrugFee, CAST(sum(average_operation_treatment_fee) AS DECIMAL(18,2)) averageOperationTreatmentFee, CAST(sum(average_disposable_consumable) AS DECIMAL(18,2)) averageDisposableConsumable, CONCAT(CAST(sum(mortality) AS DECIMAL(18,2)),'%') mortality, CONCAT(CAST(sum(operation_complication_rate) AS DECIMAL(18,2)),'%') operationComplicationRate FROM `q_single_disease_statistic_dept`
year_month_remark >= #{dateStart} and year_month_remark <= #{dateEnd}
and tqms_dept =#{deptId}
group by categoryId

mapper.select:
SELECT category_id categoryId,year_month_title yearMonth, sum(complete_report_count) completeReportCountryCount, CAST(sum(average_in_hospital_day) AS DECIMAL(18,4)) averageInHospitalDay, CAST(sum(average_in_hospital_fee) AS DECIMAL(18,2)) averageInHospitalFee, CAST(sum(mortality) AS DECIMAL(18,2)) mortality, CAST(sum(operation_complication_rate) AS DECIMAL(18,2)) complicationRate FROM `q_single_disease_statistic_dept`
year_month_remark >= #{dateStart} and year_month_remark <= #{dateEnd}
and tqms_dept =#{deptId}
and category_id = #{categoryId}
group by year_month_remark
